import React from "react";

function Toolbar({ setGrid }) {
  const applyFormatting = (command) => {
    // Apply formatting to the selected cells
    const selectedCells = document.querySelectorAll("input:focus");
    selectedCells.forEach((cell) => {
      if (command === "bold") {
        cell.style.fontWeight = cell.style.fontWeight === "bold" ? "normal" : "bold";
      } else if (command === "italic") {
        cell.style.fontStyle = cell.style.fontStyle === "italic" ? "normal" : "italic";
      } else if (command === "underline") {
        cell.style.textDecoration = cell.style.textDecoration === "underline" ? "none" : "underline";
      } else if (command === "increaseFont") {
        const currentSize = parseInt(window.getComputedStyle(cell).fontSize, 10);
        cell.style.fontSize = '${currentSize + 2}px';
      } else if (command === "decreaseFont") {
        const currentSize = parseInt(window.getComputedStyle(cell).fontSize, 10);
        cell.style.fontSize = '${currentSize - 2}px';
      }
    });
  };

  const addRow = () => {
    setGrid((prevGrid) => [...prevGrid, Array(prevGrid[0].length).fill("")]);
  };

  const addColumn = () => {
    setGrid((prevGrid) => prevGrid.map(row => [...row, ""]));
  };

  const deleteRow = () => {
    setGrid((prevGrid) => prevGrid.slice(0, prevGrid.length - 1));
  };

  const deleteColumn = () => {
    setGrid((prevGrid) => prevGrid.map(row => row.slice(0, row.length - 1)));
  };

  return (
    <div style={{ marginBottom: "10px", padding: "10px", backgroundColor: "#f1f1f1" }}>
      <button onClick={() => applyFormatting("bold")}>B</button>
      <button onClick={() => applyFormatting("italic")}>I</button>
      <button onClick={() => applyFormatting("underline")}>U</button>
      <button onClick={() => applyFormatting("increaseFont")}>A+</button>
      <button onClick={() => applyFormatting("decreaseFont")}>A-</button>
      <button onClick={addRow}>Add Row</button>
      <button onClick={addColumn}>Add Column</button>
      <button onClick={deleteRow}>Delete Row</button>
      <button onClick={deleteColumn}>Delete Column</button>
    </div>
  );
}

export default Toolbar;
